import React from "react";
import Router from "next/router";
import Sidebar from "../../../components/Partials/Sidebar";
import Header from "../../../components/Partials/Header";

const createProduct = () => {
  return (
    <>
      <Sidebar />
      <div className="main-content">
        <Header />
        <main>
          <div className="view__header">
            <a onClick={() => Router.push("/admin/products")}>
              <span className="ti-angle-left"></span>
              <small>ກັບຄືນ</small>
            </a>
            <h3 className="dash-title">ຂໍ້ມູນກ່ຽວກັບສີນຄ້າ</h3>
          </div>

          <section className="recent">
            <div className="product__info">
              {/* <h3 className="dash-title">Product Detail:</h3> */}
              <div className="product__info__card">
                <div className="card__tabs">
                  <h3>ຂໍ້ມູນທົ່ວໄປ:</h3>
                  <br />
                  <h3>ຂໍ້ມູນທົ່ວໄປ:</h3>
                  <br />
                  <h3>ຂໍ້ມູນທົ່ວໄປ:</h3>
                </div>
                <div className="card__info__form">
                {/* onSubmit={handleSubmit} */}
                  <form >
                    <div className="form-control">
                      <label htmlFor="productname">Product Name:</label>
                      <input
                        type="text"
                        name="name"
                        placeholder="Enter Product Name"
                        // value={productData.name}
                        // onChange={handleChange}
                      />
                    </div>
                    <div className="form-control">
                      <label htmlFor="address">Product Desc:</label>
                      <textarea
                        type="text"
                        name="description"
                        placeholder="Product Description"
                        // value={productData.description}
                        // onChange={handleChange}
                        rows="4"
                      />
                    </div>
                    <div className="form-control">
                      <label htmlFor="city">Price:</label>
                      <input
                        type="number"
                        name="price"
                        placeholder="Price"
                        // value={productData.price}
                        // onChange={handleChange}
                      />
                    </div>
                    <div className="form-control">
                      <label htmlFor="stockqty">Stock Qty:</label>
                      <input
                        type="number"
                        name="stockqty"
                        placeholder="Stock Qualtity"
                        // value={productData.stockqty}
                        // onChange={handleChange}
                      />
                    </div>
                    <div className="form-control">
                      <label htmlFor="contact">--Select Categories--</label>
                      <input
                        type="text"
                        name="contact"
                        placeholder="Contact No."
                      />
                    </div>
                    <div className="form-control">
                      <label htmlFor="contact">--Select Brands--</label>
                      <input
                        type="text"
                        name="contact"
                        placeholder="Contact No."
                      />
                    </div>
                    <div className="form-control">
                      <label htmlFor="contact">Product Image</label>
                      <input
                        type="file"
                        name="file"
                        placeholder="file *.png, *.jpg..."
                        // onChange={selectFile}
                      />
                    </div>
                    <div className="form-submit">
                      <button
                        className="btn__cancel"
                        onClick={() => Router.push("/admin/products")}
                      >
                        Back
                      </button>
                      <button
                        className="btn__submit"
                        type="submit"
                        // style={{
                        //   cursor:
                        //     !productData.name ||
                        //     !productData.description ||
                        //     !productData.price ||
                        //     !file ||
                        //     loading
                        //       ? "not-allowed"
                        //       : "pointer",
                        // }}
                        // disabled={
                        //   !productData.name ||
                        //   !productData.description ||
                        //   !productData.price ||
                        //   !file ||
                        //   loading
                        // }
                      >
                        {/* {loading ? "Submiting..." : ""} */}
                        Submit
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </section>
        </main>
      </div>
    </>
  );
};

export default createProduct;

