import React from 'react';
import Script from 'react-load-script'

const omiseKey = "pkey_test_5lrkpwciyz9qatu3qry"

let OmiseCard;



const CheckoutWithCreditCard = ({carts}) => {
        const amount = carts.reduce((sum, cart) => sum + cart.qualtity * cart.product.price, 0)
        console.log("OMISE KEY:", omiseKey)


        const  handleLoadScript = () => {
            OmiseCard = window.OmiseCard
            OmiseCard.configure({
                publicKey: omiseKey,
                current: 'thb',
                frameLabel: 'Ozone Shoppy',
                submitLabel: 'Pay Now!',
                buttonLabel: 'Pay With Omise'
            })
        }

        const creditCardConfigure = () => {
            OmiseCard.configure({
                defaultPaymentMethod: 'credit_card',
                otherPaymentMethod: []
            })
            OmiseCard.configureButton("#credit-card")
            OmiseCard.attach()
    }


    const omiseCardHandler = () => {
        //const { cart, user, createCreditCardcharge } = props;
        OmiseCard.open({
            frameScription: 'Invoice #8723',
            amount: amount * 100,
            onCreateTokensuccess: token => {
                console.log(token)
                //createCreditCardcharge(user.email, user.username, cart.amount, token)
            },
            onFormClosed: () => {}
        })
    }


    const handleClick = () => {
        creditCardConfigure()
        omiseCardHandler()
    }
    return (
        <div className="own-form">
           <Script url='https://cdn.omise.co/omise.js' onLoad={handleLoadScript} />
            <form action="">
                <button
                    id="credit-card"
                    className="btn-payment__omise"
                    type="button"
                    disabled={!amount}
                    onClick={handleClick}
                >
                    Pay Now! With Omise
                </button>
            </form>
        </div>
    )
}

export default CheckoutWithCreditCard;
