import React, { useContext } from "react";
import Sidebar from "../../../components/Partials/Sidebar";
import Header from "../../../components/Partials/Header";
import { AuthContext } from "../../../contexts/AuthProvider";
import ProductItems from "../../../components/UserProduct/ProductItems";
import Router from "next/router";
// import { MY_PRODUCTS } from '../../../graphql/Products';
// import { useQuery } from '@apollo/react-hooks';


const UserProducts = () => {
  const { user } = useContext(AuthContext);
  // const {data, loading, error} = useQuery(MY_PRODUCTS);
  // console.log("User Products:", user);
  // if (error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  // if (loading) return <p>loading...</p>
  // console.log(user)

  return (
    <>
      <Sidebar />
      <div className="main-content">
        <Header />
        <main>
          {/* <UserProduct /> */}
          <div className="view__header">
            <h3 className="dash-title">User Products Over Views</h3>
            <a onClick={() => Router.push("/admin/products/create")}>
              <span className="ti-plus"></span>
              <small>Add Product</small>
            </a>
          </div>

          <section className="recent">
            <div className="activity-card">
              <h3>Recently Activity</h3>
              <div className="table-responsive">
                <table>
                  <thead>
                    <tr>
                      <th>Image</th>
                      <th>Products Name</th>
                      <th>Description</th>
                      <th>Price</th>
                      <th>Stock Qty</th>
                      <th>Unit</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      user &&
                      user.products.map(product => (
                        <ProductItems key={product.id} product={product} />
                      ))
                    }
                  </tbody>
                </table>
              </div>
            </div>
          </section>
        </main>
      </div>
    </>
  );
};

export default UserProducts;
