import React, { useContext } from 'react';
import Router from 'next/router';
import { AuthContext } from '../../contexts/AuthProvider';
import { useMutation } from '@apollo/react-hooks';
import { ADD_TO_CART } from '../../graphql/Carts';
import { MY_PRODUCTS } from '../../graphql/Products';


const Products = ({prod}) => {
    const { user } = useContext(AuthContext);
    console.log("user", user)
    const [addToCart, { loading, error }] = useMutation(ADD_TO_CART, {
        onCompleted: data => {
            return data;
        },
        refetchQueries: [{ query: MY_PRODUCTS }]
    })
    const handleAddToCart = async (id) => {
        console.log('product_id:', id)
        if (!user) {
            return Router.push('/signin')
        }   
        await addToCart({ variables: { id } })
    }
    return (
        <>
            <div className="product">
                <div className="img__container">
                    <img src={prod.imageUrl} alt={prod.description} />
                    <div className="addCart">
                        <i className="ti-shopping-cart" onClick={() => handleAddToCart(prod.id)}/>
                    </div>
                    <ul className="side__icons">
                        <span><i className="ti-search" /></span>
                        <span><i className="ti-heart" /></span>
                        <span><i className="ti-layout-slider" /></span>
                    </ul>
                </div>
                <div className="bottom">
                    <a href="/Products/product_detail"  as={`/products/${prod.id}`}>{prod.name}</a>
                    <div className="price">
                        <span>${prod.price}</span>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Products;
